import { Link, Route, BrowserRouter as Router, Switch } from "react-router-dom";

import React from 'react';
import img1 from '../Images/WM1.jpg';
import styled from 'styled-components';

const Card = styled.div`
    display: flex;
    flex-direction: column;
    width: 300px;
    border: solid 1px #28b5b5;
    background-color: white;
    padding: 10px;
    margin: 10px;
    transition: 0.5s;
    :hover {
        box-shadow:
            0 2.8px 2.2px rgba(0, 0, 0, 0.034),
            0 6.7px 5.3px rgba(0, 0, 0, 0.048),
            0 12.5px 10px rgba(0, 0, 0, 0.06),
            0 22.3px 17.9px rgba(0, 0, 0, 0.072),
            0 41.8px 33.4px rgba(0, 0, 0, 0.086),
            0 100px 80px rgba(0, 0, 0, 0.12)
        ;
    }
`;
const Groupping = styled.span`
    display: flex;
    justify-content: flex-end
`;
const Img = styled.img`
    width: 100%;
    max-width: 500px;
`;
const Title = styled.h2`
    margin: 0;
    padding: 0;
    text-transform: uppercase;
    color: #4b778d;
`;
const Owner = styled.h4`
    margin: 0 0 15px 0;
    padding: 0;
    color: #4b778d;
`;
const Button = styled.button`
    background: ${props => props.detail ? "#ffffff" : "#28b5b5"};
    color: ${props => props.detail ? "#28b5b5" : "#ffffff"};
    font-size: 1em;
    margin: 0 0 0 10px;
    padding: 0.5em 2em;
    border: 2px solid #28b5b5;
    border-radius: 3px;
    cursor: pointer;

    :hover {
        text-decoration: underline;
    }
`;
const Section = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: baseline;
`;
const Qty = styled.h4`
    margin: 0;
    padding: 0;
    color: #4b778d;
`;
const example = [
    {
        "_id": "1",
        "title": "Nike Shoes",
        "src": [
            "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img4.jpg"
            ],
        "description": "UI/UX designing, html css tutorials sfj;asdjkfsj;afljklasdfjlasfjlkasjdfkl;sjkfl;",
        "content": "Welcome to our channel Dev AT. Here you can learn web designing, UI/UX designing, html css tutorials, css animations and css effects, javascript and jquery tutorials and related so on.",
        "price": 23,
        "colors":["red","black","crimson","teal"],
        "count": 1
    },
    {
        "_id": "3",
        "title": "Adidas Shoes",
        "src": [
            "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img4.jpg"
        ],
        "description": "Nothing else matters",
        "content": "Just a test",
        "price": 25,
        "colors": ["red", "yellow"],
        "count": 2
    }
]

export default function Item(props) {
    const { object } = props;
    console.log(object)

    return (
        <React.Fragment>

                <Card>
                    <Img src={obj.src} />
                    <Section>
                        <Title>{obj.title}</Title>
                    </Section>
                    <Qty>จำนวน : {obj.count} เล่ม</Qty>
                    <Owner>เจ้าของ : {obj.description}</Owner>
                    <Groupping>
                        <Link to='/detail'>
                            <Button detail>
                                รายละเอียด
                            </Button>
                        </Link>

                        <Button>รับ</Button>
                    </Groupping>
                </Card>

        </React.Fragment>
    )
}
