import React from 'react';
import img1 from '../Images/WM1.jpg';
import styled from 'styled-components';

const Container = styled.div`
    margin: 60px 150px;
    display: flex;
    justify-content: center;
`;
const Card = styled.div`
    display: flex;
    flex-flow: column wrap;
    justify-content: center;
    align-items: center;
    border: solid 1px #4b778d;
    width: 1000px;
    transition: 0.5s;
    background-color: #ffffff;
    :hover {
        box-shadow:
            0 2.8px 2.2px rgba(0, 0, 0, 0.034),
            0 6.7px 5.3px rgba(0, 0, 0, 0.048),
            0 12.5px 10px rgba(0, 0, 0, 0.06),
            0 22.3px 17.9px rgba(0, 0, 0, 0.072),
            0 41.8px 33.4px rgba(0, 0, 0, 0.086),
            0 100px 80px rgba(0, 0, 0, 0.12)
        ;
    }
`;
const Page = styled.h1`
    text-transform: uppercase;
    margin: 30px 0 80px;
`;
const Selected = styled.div`
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    width: 100%;

    @media (max-width: 770px) {
        flex-flow: column wrap;
        justify-content: flex-start;
        align-items: center;
    }
`;
const Img = styled.img`
    width: 100%;
    max-width: 300px;
    max-height: 300px;
`;
const Title = styled.h3`
    text-transform: uppercase;
    color: #4b778d;
    
    @media (min-width: 769px) {
        margin: 0 10px;
    }
`;
const Button = styled.button`
    cursor: pointer;
    color: ${props => props.confirm ? "#4b778d" : "tomato"};
    font-size: 1em;
    margin: 0 0 0 10px;
    padding: 0.5em 2em;
    margin: ${props => props.confirm ? "80px 0 30px" : "0"};
    border: ${props => props.confirm ? "solid 2px #4b778d" : "solid 2px tomato"};
    border-radius: 3px;
    width: ${props => props.confirm ? "50%" : "0"};

    @media (max-width: 770px) {
        display: ${props => props.confirm ? "" : "none"};
        margin: 10px 0 30px;
        padding: 0.5em 2em;
    }
`;
const DescSection = styled.div`
    display: flex;
    flex-flow: column wrap;

    @media (max-width: 770px) {
        justify-content: center;
        align-items: center;
    }
`;
const DeleteButton = styled.button`
    cursor: pointer;
    color: tomato;
    font-size: 1em;
    padding: 0.5em 2em;
    border: solid 2px tomato;
    border-radius: 3px;
    width: 50%;


    @media (min-width: 769px) {
        display: none;
    }
`;
const QtySection = styled.div`
    display: flex;
    flex-flow: column wrap;
    align-items: center;

    @media (max-width: 770px) {
        margin: 10px 0;
    }
`;

const example = [
    {
        "_id": "1",
        "title": "Nike Shoes",
        "src": [
            "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img4.jpg"
            ],
        "description": "UI/UX designing, html css tutorials",
        "content": "Welcome to our channel Dev AT. Here you can learn web designing, UI/UX designing, html css tutorials, css animations and css effects, javascript and jquery tutorials and related so on.",
        "price": 23,
        "colors":["red","black","crimson","teal"],
        "count": 1
    }
]

export default function Basket() {
    return (
        <React.Fragment>

            {example.map(item => (
                <Container>
                    <Card>
                    <Page>ตะกร้าหนังสือ</Page>
                        <Selected>
                            <Img src={img1} />

                            <DescSection>
                                <Title>{item.title}</Title>
                                <div style={{margin: '0 10px'}}>{item.description}</div>
                            </DescSection>

                            <QtySection>
                                <Title>จำนวน</Title>
                                <span>{item.count}</span>
                            </QtySection>

                            <div>
                                <Button>ลบ</Button>
                            </div>
                        </Selected>

                        <DeleteButton>ลบ</DeleteButton>
                        <Button confirm>ต่อไป</Button>
                    </Card>
                </Container>
            ))}

        </React.Fragment>
    )
}
