import React from 'react';
import styled from 'styled-components';

const Nav = styled.nav`
    padding: 0 20px;
    min-height: 9vh;
    background: #ffffff;
    display: flex;
    justify-content: space-between;
    align-items: center;

    @media (min-width: 769px) {
        justify-content: center;
    }
`;
const DesktopLogo = styled.h1`
    font-size: 40px;
    color: #4b778d;
    border: 1px solid #4b778d;
    background-color: #8fd9a8;
    padding: 5px;

    @media (max-width: 770px) {
        display: none;
    }
`;
const MobileLogo = styled.h1`
    font-size: 30px;
    color: #4b778d;
    border: 1px solid #4b778d;
    padding: 5px;

    @media (min-width: 769px) {
        display: none;
    }
`;
const Menu = styled.ul`
    list-style: none;
    display: flex;

    li:nth-child(2) {
        margin: 0px 20px;
    }

    @media (max-width: 768px) {
        display: none;
    }
`; 
const Item = styled.li``;
const Link = styled.a`
    font-size: 20px;
    color: #4b778d;
    text-decoration: none;
    :hover {
        text-decoration: underline;
    }
`;
const NavIcon = styled.button`
    background: none;
    cursor: pointer;
    border: none;
    outline: none;

    @media (min-width: 769px) {
        display: none;
    }
`;
const Line = styled.span`
    display: block;
    border-radius: 50px;
    width: 25px;
    height: 3px;
    margin: 5px;
    background-color: #4b778d;
    transition: width 0.4s ease-in-out;

    :nth-child(2) {
        width: ${props => (props.open ? "40%" : "70%")};
    }
`;
const Overlay = styled.div`
    position: absolute;
    height: ${props => (props.open ? "91vh" : 0)};
    width: 100vw;
    background: #ffffff;
    transition: height 0.4s ease-in-out;

    @media (min-width: 769px) {
        display: none;
    }
`;
const OverlayMenu = styled.ul`
    list-style: none;
    position: absolute;
    left: 50%;
    top: 45%;
    transform: translate(-50%, -50%);

    li {
        opacity: ${props => (props.open ? 1 : 0)};
        font-size: 25px;
        margin: 50px 0px;
        transition: opacity 0.4s ease-in-out;
    }

    li:nth-child(2) {
        margin: 50px 0px;
    }
`;

export default function Navbar() {
    const [toggle, setToggleNav] = React.useState(false);

    return (
        <div>

            <div style={{display: 'flex', justifyContent: 'center'}}>
                <DesktopLogo>Sharerity</DesktopLogo>
            </div>
            <Nav>
                <MobileLogo>Sharerity</MobileLogo>

                <Menu>
                    <Item>
                        <Link target="#" href="https://www.instagram.com/igor_dumencic/">
                            Instagram
                        </Link>
                    </Item>
                    <Item>
                        <Link target="#" href="https://www.behance.net/igordumencic">
                        Behance
                        </Link>
                    </Item>
                    <Item>
                        <Link target="#" href="https://github.com/Igor178">
                        เข้าสู่ระบบ
                        </Link>
                    </Item>
                </Menu>

                <NavIcon onClick={() => setToggleNav(!toggle)}>
                    <Line open={toggle} />
                    <Line open={toggle} />
                    <Line open={toggle} />
                </NavIcon>
            </Nav>

            <Overlay open={toggle}>
                <OverlayMenu open={toggle}>
                    <Item>
                        <Link target="#" href="https://www.instagram.com/igor_dumencic/">
                        Instagram
                        </Link>
                    </Item>
                    <Item>
                        <Link target="#" href="https://www.behance.net/igordumencic">
                        Behance
                        </Link>
                    </Item>
                    <Item>
                        <Link target="#" href="https://github.com/Igor178">
                        เข้าสู่ระบบ
                        </Link>
                    </Item>
                </OverlayMenu>
            </Overlay>

        </div>
    )
}
