import React from 'react';
import img1 from '../Images/WM1.jpg';
import styled from 'styled-components';

const Card = styled.div`
    display: flex;
    flex-direction: column;
    width: 300px;
    border: solid 1px #4b778d;
    background-color: white;
    padding: 10px;
    margin: 10px;
`;
const Groupping = styled.span`
    display: flex;
    justify-content: flex-end
`;
const Img = styled.img`
    width: 100%;
    max-width: 500px;
`;
const Title = styled.h2`
    margin: 0;
    padding: 0;
    text-transform: uppercase;
    color: #28b5b5;
`;
const Owner = styled.h4`
    margin: 0 0 15px 0;
    padding: 0;
    color: #28b5b5;
`;
const Button = styled.button`
    background: ${props => props.detail ? "#ffffff" : "#4b778d"};
    color: ${props => props.detail ? "#4b778d" : "#ffffff"};
    font-size: 1em;
    margin: 0 0 0 10px;
    padding: 0.5em 2em;
    border: 2px solid #4b778d;
    border-radius: 3px;
`;
const Section = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: baseline;
`;
const Qty = styled.h4`
    margin: 0;
    padding: 0;
    color: #28d5b5;
`;

export default function Item() {
    return (
        <div>

            <Card>
                <Img src={img1} />
                <Section>
                    <Title>World Map3</Title>
                </Section>
                <Qty>จำนวน 1 เล่ม</Qty>
                <Owner>Owner : David</Owner>
                <Groupping>
                    <Button detail>รายละเอียด</Button>
                    <Button>รับ</Button>
                </Groupping>
            </Card>

        </div>
    )
}
