import { useDispatch, useSelector } from 'react-redux';

import { Link } from 'react-router-dom';
import LoadingScreen from './LoadingScreen';
import MessageScreen from './MessageScreen';
import React from 'react';
import { addToBasket } from './Redux/Actions/BasketActions';
import img1 from '../Images/WM1.jpg';
import styled from 'styled-components';

const Container = styled.div`
    margin: 60px 150px;
    display: flex;
    justify-content: center;
`;
const Card = styled.div`
    display: flex;
    flex-flow: column wrap;
    justify-content: center;
    align-items: center;
    border: solid 1px #4b778d;
    width: 1000px;
    transition: 0.5s;
    background-color: #ffffff;
    :hover {
        box-shadow:
            0 2.8px 2.2px rgba(0, 0, 0, 0.034),
            0 6.7px 5.3px rgba(0, 0, 0, 0.048),
            0 12.5px 10px rgba(0, 0, 0, 0.06),
            0 22.3px 17.9px rgba(0, 0, 0, 0.072),
            0 41.8px 33.4px rgba(0, 0, 0, 0.086),
            0 100px 80px rgba(0, 0, 0, 0.12)
        ;
    }
`;
const Page = styled.h1`
    text-transform: uppercase;
    margin: 30px 0 80px;
`;
const Selected = styled.div`
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    width: 100%;

    @media (max-width: 770px) {
        flex-flow: column wrap;
        justify-content: flex-start;
        align-items: center;
    }
`;
const Img = styled.img`
    width: 100%;
    max-width: 300px;
    max-height: 300px;
`;
const Title = styled.h3`
    text-transform: uppercase;
    color: #4b778d;
    
    @media (min-width: 769px) {
        margin: 0 10px;
    }
`;
const Button = styled.button`
    cursor: pointer;
    color: ${props => props.confirm ? "#4b778d" : "tomato"};
    font-size: 1em;
    margin: 0 0 0 10px;
    padding: 0.5em 2em;
    margin: ${props => props.confirm ? "80px 0 30px" : "0"};
    border: ${props => props.confirm ? "solid 2px #4b778d" : "solid 2px tomato"};
    border-radius: 3px;
    width: ${props => props.confirm ? "50%" : "0"};

    @media (max-width: 770px) {
        display: ${props => props.confirm ? "" : "none"};
        margin: 10px 0 30px;
        padding: 0.5em 2em;
    }
`;
const DescSection = styled.div`
    display: flex;
    flex-flow: column wrap;

    @media (max-width: 770px) {
        justify-content: center;
        align-items: center;
    }
`;
const DeleteButton = styled.button`
    cursor: pointer;
    color: tomato;
    font-size: 1em;
    padding: 0.5em 2em;
    border: solid 2px tomato;
    border-radius: 3px;
    width: 50%;


    @media (min-width: 769px) {
        display: none;
    }
`;
const QtySection = styled.div`
    display: flex;
    flex-flow: column wrap;
    align-items: center;

    @media (max-width: 770px) {
        margin: 0 0 10px;
        display: flex;
        flex-direction: rows;
    }
`;

export default function Basket(props) {
    const itemId = props.match.params.id;
    const dispatch = useDispatch();
    const basket = useSelector(state => state.basket);
    const { basketItems } = basket;

    React.useEffect(() => {
        if(itemId) {
            dispatch(addToBasket(itemId));
        }
    }, [dispatch, itemId]);

    return (
        <React.Fragment>

                <Container>
                    {basketItems.length === 0 ? (
                        <MessageScreen>
                            ตะกร้าหนังสือยังว่างเปล่าอยู่ รับบริจาคหนังสือซักหน่อยไหม?
                            <Link t='/'>ไปรับบริจาคหนังสือ</Link>
                        </MessageScreen>
                    ) : (
                    
                    <>
                        {basketItems.map(item => (
                            <Card key={item.id}>
                                <Page>ตะกร้าหนังสือ</Page>
                                <Selected>
                                    <Img src={item.src} />

                                    <DescSection>
                                        <Title>{item.title}</Title>
                                        <div style={{margin: '0 10px'}}>{itemId}</div>
                                    </DescSection>

                                    <QtySection>
                                        <Title>จำนวน</Title>
                                        <span>{item.count}</span>
                                    </QtySection>

                                    <div>
                                        <Button>ลบ</Button>
                                    </div>
                                </Selected>

                                <DeleteButton>ลบ</DeleteButton>
                                <Button confirm>ต่อไป</Button>
                            </Card>
                        ))}
                    </>

                    )}
                </Container>

        </React.Fragment>
    )
}
