import { Link, Route, BrowserRouter as Router, Switch, useParams } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';

import LoadingScreen from './LoadingScreen';
import MessageScreen from './MessageScreen';
import React from 'react';
import img1 from '../Images/WM1.jpg';
import styled from 'styled-components';

const OuterContainer = styled.div`
    display: flex;
    flex-wrap: wrap;
    height: 100%;
    padding: 60px 150px;
    background-color: #ffffff;
`;
const Card = styled.div`
    width: 100%;
    min-width: 1100px;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
    border: 1px solid #4b778d;
    
    @media (max-width: 770px) {
        flex-wrap: wrap;
    }
`;
const Img = styled.img`
    width: 100%;
    padding: 50px;
    // max-width: 500px;
    // max-height: 300px;
`;
const Section = styled.div`
    width: 100%;
    padding: 50px;
    // background-color: gray;
`;
const Title = styled.h2`
    color: #4b778d;
    text-transform: upppercase;
    display: inline-block;
    padding: 0;
    margin: 0;
`;
const Button = styled.button`
    background: ${props => props.detail ? "#ffffff" : "#4b778d"};
    color: ${props => props.detail ? "#4b778d" : "#ffffff"};
    font-size: 1em;
    margin: 20px 0;
    padding: 0.5em 2em;
    border: 2px solid #4b778d;
    border-radius: 3px;
`;
const Breadcrumb = styled.div`
    display: flex;
    flex-flow: rows wrap;
    justify-content: flex-start;
    padding: 10px;
    background-color: #8fd9a8;
    color: #ffffff;
    border: 1px solid #8fd9a8;
    margin: 0 0 10px;
`;
const BreadUl = styled.ul`
    letter-spacing: 1px;
    color: #4b778d;
    margin: 0;
    padding: 0;
    display: inline-flex;
    list-style: none;
    margin-right: 5px;
`;
const BreadLi = styled.li`
    margin-right: 5px;
`;
const example = [
    {
        "_id": "1",
        "title": "Nike Shoes",
        "src": [
            "https://www.upsieutoc.com/images/2020/06/27/img1.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img2.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img3.jpg",
            "https://www.upsieutoc.com/images/2020/06/27/img4.jpg"
            ],
        "description": "UI/UX designing, html css tutorials",
        "content": "Welcome to our channel Dev AT. Here you can learn web designing, UI/UX designing, html css tutorials, css animations and css effects, javascript and jquery tutorials and related so on.",
        "price": 23,
        "colors":["red","black","crimson","teal"],
        "count": 1
    }
]

export default function DetailItem(props) {
    const itemId = useParams;
    const dispatch = useDispatch();
    const itemDetails = useSelector(state => state.itemDetails);
    const { loading, error, items } = itemDetails;

    React.useEffect(() => {
        dispatch(itemDetail(itemId));
    }, []);

    return (
        <React.Fragment>

            
            {loading ? (
                <LoadingScreen />
                ) : error ? (
                <MessageScreen>{error}</MessageScreen>
                ) : (
                
                <OuterContainer>
                    <Breadcrumb>
                        <BreadUl>
                            <Link to='/' style={{color: '#4b778d'}}>
                                <BreadLi>หน้าหลัก</BreadLi>
                            </Link>
                            <BreadLi>/</BreadLi>
                            <BreadLi>หนังสือ</BreadLi>
                        </BreadUl>
                    </Breadcrumb>
                            
                    {example.map(item => (
                        <Card>
                            <Img src={img1} />
                            <Section>
                                <Title>{item.title}</Title>
                                <h5 style={{color: '#4b778d'}}>Owner : {item.description}</h5>
                                <div style={{color: '#4b778d'}}>
                                    {item.content}
                                </div>
                                <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'flex-end' }}>
                                    <Button>รับ</Button>
                                </div>
                            </Section>
                        </Card>
                    ))}
                </OuterContainer>

            )}

        </React.Fragment>
    )
}
